mapper.select:
selectfrom TSSO_AUTHORITY
         and AUTH_OBJ_ID like #{authObjId} 
         and AUTH_OBJ_TYPE like #{authObjType} 
         and AUTH_OWNER_ID like #{authOwnerId} 
         and AUTH_OWNER_TYPE like #{authOwnerType} 
         and AUTH_SYSTEM_ID like #{authSystemId} 
         and AUTH_CREATOR_ID like #{authCreatorId} 
         and AUTH_CREATOR_ORGAN_ID like #{authCreatorOrganId} 
         and AUTH_ORGAN_ID like #{authOrganId} 
         and AUTH_DESC like #{authDesc} 
    ORDER by  DESC
