mapper.select:
selectfrom TSSO_MENU
         and MENU_NAME like #{menuName} 
         and MENU_SYSTEM_ID like #{menuSystemId} 
         and MENU_TEXT like #{menuText} 
         and MENU_VALUE like #{menuValue} 
         and MENU_TYPE like #{menuType} 
         and MENU_CREATOR_ID like #{menuCreatorId} 
         and MENU_ORGAN_ID like #{menuOrganId} 
         and MENU_ORGAN_NAME like #{menuOrganName} 
         and MENU_IS_EXPAND like #{menuIsExpand} 
         and MENU_IS_SYSTEM like #{menuIsSystem} 
         and MENU_LEFT_URL like #{menuLeftUrl} 
         and MENU_LEFT_URL_PARA like #{menuLeftUrlPara} 
         and MENU_RIGHT_URL like #{menuRightUrl} 
         and MENU_RIGHT_URL_PARA like #{menuRightUrlPara} 
         and MENU_PARENT_ID like #{menuParentId} 
         and MENU_PARENT_ID_LIST like #{menuParentIdList} 
         and MENU_TARGET like #{menuTarget} 
         and MENU_UPDATER like #{menuUpdater} 
         and MENU_USE_ORGAN_NAME like #{menuUseOrganName} 
         and MENU_USE_ORGAN_ID like #{menuUseOrganId} 
         and MENU_USE_ORGAN_TYPE_NAME like #{menuUseOrganTypeName} 
         and MENU_USE_ORGAN_TYPE_ID like #{menuUseOrganTypeId} 
         and MENU_CLASS_NAME like #{menuClassName} 
         and MENU_CLASS_NAME_HOVER like #{menuClassNameHover} 
         and MENU_CLASS_NAME_SELECTED like #{menuClassNameSelected} 
         and MENU_DESCRIPTION like #{menuDescription} 
         and MENU_STATUS like #{menuStatus} 
         and MENU_JS_EXPRESSION like #{menuJsExpression} 
    ORDER by MENU_ORDER_NUM DESC
